//importo de librerias externas
import React from 'react'
import Box from '@mui/material/Box'
import Snackbar from '@mui/material/Snackbar'
import IconButton from '@mui/material/IconButton'
import CloseIcon from '@mui/icons-material/Close'
import userActions from '../redux/actions/userActions'

//importo acciones de redux
import {connect} from 'react-redux'
import {useDispatch, useSelector} from 'react-redux'

function Alerts() {
    const dispatch = useDispatch()
    const handleClose = () => {
        dispatch({
            type: 'MESSAGE',
            payload: {view: false, message: '', success: false}
        })
    }

    const snackbar = useSelector(store=>store.userReducer.snackbar)
    // console.log(snackbar);
    const action = (
        <Box sx={{
            width: '100%',
            backgroundColor: snackbar.success ? 'green':'red',
            color: 'white',
            borderRadius: '4px',
            padding: '4px',
            fontWeight: '400'}}>
            {(typeof snackbar.message) === "string" ?
                (<p>{snackbar.message}</p>) :
                <div>{snackbar.message.map((message,index) =><p key={index}>{message.message}</p>)}</div>
            }

        </Box>
    )

    // console.log(snackbar.view);
    return (
        <Snackbar
            open={snackbar.view}
            autoHideDuration={5000}
            onClose={handleClose}
            action={action}
            message={
                <IconButton size="small" aria-label="close" color="inherit" onClick={handleClose}>
                    <CloseIcon fontSize="small" />
                </IconButton>
            } 
        />
    )
}

// const mapStateToProps = (state) => {
//     return {
//         snackbar: state.userReducer.snackbar,
//     }
// }

// export default connect(mapStateToProps, null)(Alerts)
export default Alerts
