import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/grid";
import "swiper/css/pagination";
import data from "../data.json"
import "../styles/slider.css";

// import required modules
import { Autoplay, Navigation, Grid, Pagination } from "swiper";

export default function Slider() {
  return (
    <>
      <Swiper
        slidesPerGroup={2}
        slidesPerView={2}
        grid={{
          rows: 2,
        }}
        spaceBetween={30}
        pagination={{
          clickable: true,
        }}
        modules={[Grid, Pagination]}
        className="mySwiper"
      >
        { data.map(ciudades =>{ 
            return(
            // Las KEYS ayudan a indentificar que items han cambiado, son agregados o eliminados. Deben ser daas a los elementos dentro del array para darle a los elementos una identidad estable.
            <SwiperSlide className= "mySwiper" style={{backgroundImage:`url("${ciudades.imagen}")`, backgroundSize:"cover"}}> 
              <div className="nombre_ciudad">
                {/* <img className="imagen_ciudad" src={ciudades.imagen} alt={ciudades.ciudad}/> */}
                <p>{ciudades.ciudad}</p>
                <p>{ciudades.pais}</p>
              </div>
            </SwiperSlide>
        )})
        }
      </Swiper>
    </>
  );
}
