import React, { useEffect } from "react";
import itinerariesActions from "../redux/actions/itinerariesActions";
import { useDispatch, useSelector } from "react-redux"
import { useParams } from "react-router-dom";
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';
import "../styles/itineraries.css"
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { styled } from '@mui/material/styles';
import IconButton from '@mui/material/IconButton';
import activitiesActions from "../redux/actions/activitiesActions"
import Activities from "./Activities";
let emoji = "ðŸ’µ"

const ExpandMore = styled((props) => {
    const { expand, ...other } = props;
    return <IconButton {...other} />;
    })(({ theme, expand }) => ({
    transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
    }),
}));



function Itineraries(){
    const {id}= useParams()
    const dispatch = useDispatch()
    useEffect(()=>{
        dispatch(itinerariesActions.findTinFromCity(id))
    },[])
    const itinerary = useSelector(store=>store.itinerariesReducer.itinerary)
    const user = useSelector(store=>store.userReducer.user)
    // Button for expand activities
    const [expanded, setExpanded] = React.useState(false);
    const handleExpandClick = () => {
        setExpanded(!expanded);
    };
    async function likes() {
        await likeDislike(itinerary._id)    
        setReload(!reload)
    }
    return(
            <div className="itineraries-container">
                {itinerary.length>0?
                itinerary.map(itinerary=>(
                <div className="itinerary">
                    <div className="itinerary-box-one">
                        <div className="box-photo">
                            <img src={itinerary.personPhoto} alt="photo" className="photo-itinerary"/>
                        </div>
                        <div className="person-name">
                            <p>{itinerary.person}</p>
                        </div>
                    </div>
                    <div className="itinerary-box-two">
                        <div className="itinerary-body">
                            <h3>{itinerary.itineraryName}</h3>
                            <p>{emoji.repeat(itinerary.price)}</p>
                            <p>{itinerary.duration}</p>
                            <div className="likes">
                                <p>{itinerary.likes.length} <FavoriteBorderIcon /> Likes</p>
                            {/* <p>{itinerary.likes.length} <FavoriteIcon /> Likes</p> */}
                            {props.user ?
              (<button onClick={likesOrDislikes}>{place?.likes.includes(props.user.id) ?
                <span style={{ color: "red", fontSize:30 }} class="material-icons">favorite</span> :
                <span style={{  fontSize:30 }}class="material-icons">favorite_border</span>}</button>)

              : (<span style={{  fontSize:30 }} class="material-icons">favorite_border</span>)}
                            </div>
                            <p>See Activities</p>
                            <ExpandMore
                                expand={expanded}
                                onClick={handleExpandClick}
                                aria-expanded={expanded}
                                aria-label="show more">
                                <ExpandMoreIcon />
                            </ExpandMore>
                        </div>
                    </div>
                    <div className="itinerary-box-three">
                        {/* <p>{itinerary.hashtags}</p> */}
                        {itinerary.hashtags.map(hashtags=>(
                            <p className="hashtag-css">{hashtags}</p>
                        ))}
                    </div>
                    <div className="activities">
                        <Activities/>
                    </div>
                </div>
            )): <div className="error-message">
                    <p className="noitineraries">No itineraries available</p>
                </div>
            }
            </div>
    )
}
export default Itineraries
