import React, { useEffect } from 'react'
import jwt_decode from "jwt-decode"
import { useDispatch } from "react-redux"
import userActions from '../redux/actions/userActions'
import { useRef } from 'react';
import {GoogleLogin} from "react-google-login";
import ReactDOM  from 'react-dom';

export default function GoogleSignUp() {
    const dispatch = useDispatch();
    // const gbutton = useRef("g-button")

    async function handleCallbackResponse(response){
        console.log(response.credential);
        let userObject = jwt_decode(response.credential)
        console.log(userObject);
        dispatch(userActions.signUp({
            fullname: userObject.name,
            email: userObject.email,
            password: userObject.jti,
            country: "Argentina",
            photo: userObject.picture,
            from: "Google Sign Up"
        }))
    }
    // useEffect(()=>{
    //     google.account.id.initialize({
    //         client_id:"802070442112-p8eqcc02hidtdhhprf04nrhem1d2mdek.apps.googleusercontent.com",
    //         callback: handleCallbackResponse
    //     })
    //     google.accounts.id.renderButton(
    //         document.getElementById("g-button"),
    //         gbutton,
    //         { theme: "outline", size: "medium" }
    //     )
    // })
    return (
        <div>
            <GoogleLogin
            /*fields = "name,email,id,picture"*/
            clientId="176379756284-u8qru9aq48o2lq5k9u9ubrnt3cm2c7o7.apps.googleusercontent.com"
            onSuccess={handleCallbackResponse}
            onFailure={handleCallbackResponse}
            cookiePolicy={'single_host_origin'}
            render = { renderProps => (
                <>
                <IconButton onClick={renderProps.onClick} sx={{bgcolor: 'rgb(165, 126, 196)', color: 'white', height: '40px', '&:hover': {bgcolor: 'rgba(0, 0, 0, 0.5)'}}}>
                    <GoogleIcon />
                </IconButton>
                </>
            )}
        />
        </div>
    )
}
