import React, {useState} from "react"
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import itinerariesActions from "../redux/actions/itinerariesActions";
import commentActions from "../redux/actions/commentsActions"
import AddIcon from "@mui/icons-material/Add"

function Comments({itinerary, cityid}){
    console.log(itinerary);
    const [comments, setComments] = useState([])
    const [inputText, setInputText] = useState()
    const [modify, setModify] = useState()
    const [reload, setReload] = useState()
    const dispatch = useDispatch()
    
    const user = useSelector(store=>store.userReducer.user)

    // let itineraryId = itinerary._id

    async function toAdd(event) {
        const commentData = {
            itineraryId: itinerary._id,
            comments: {
                comment: inputText,
                userid: user.id
            },
        }
        let comment = await dispatch(commentActions.addComment(commentData))
        setInputText(comment)
        setReload(!reload)
    }
    useEffect(()=>{
        dispatch(itinerariesActions.findTinFromCity(cityid))
    },[reload])
    return(
        <div>
            <p> god no?</p>
            <textarea rows='2' onChange={(event) => setInputText(event.target.value)} value={inputText} className='myInputforComment fredokaFont' />
            <AddIcon onClick={toAdd} sx={{bgcolor: 'rgb(196, 165, 126)', '&:hover': {bgcolor: 'rgba(196, 165, 126, 0.7)'}, padding: '5px', margin: '5px', marginRigth: '0', color: 'white', width: '30px', height: '30px', borderRadius: '15px'}} />
        </div>
    )
}
export default Comments
