import React, {useState} from "react"
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import itinerariesActions from "../redux/actions/itinerariesActions";
import commentActions from "../redux/actions/commentsActions"
import AddIcon from "@mui/icons-material/Add"
import { TextField } from "@mui/material";

function Comments({itinerary, cityid}){
    console.log(itinerary);
    const [comments, setComments] = useState([])
    const [inputText, setInputText] = useState()
    const [modify, setModify] = useState()
    const [reload, setReload] = useState()
    const dispatch = useDispatch()
    console.log(inputText);
    const user = useSelector(store=>store.userReducer.user)

    // let itineraryId = itinerary._id

    async function toAdd(event) {
        const commentData = {
            itineraryId: itinerary._id,
            comments: {
                comment: inputText,
                userid: user.id
            },
        }
        await dispatch(commentActions.addComment(commentData))
        setInputText("")
        setReload(!reload)
    }
    async function toModify(event){
        const commentData = {
            commentId : event.taget.id,
            comments: {
                comment: modify,
                userid: user.id
            }
        }
        await dispatch(commentActions.modifyComment(commentData))
        setReload(!reload)
    }

    useEffect(()=>{
        dispatch(itinerariesActions.findTinFromCity(cityid))
    },[reload])
    return(
        <div>
            <p> god no?</p>
            <textarea rows='2' onChange={(event) => setInputText(event.target.value)} value={inputText} className='myInputforComment fredokaFont' />
            <AddIcon onClick={toAdd}  />
        </div>
    )
}
export default Comments
