import React, {useState} from "react"
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import itinerariesActions from "../redux/actions/itinerariesActions";
import commentActions from "../redux/actions/commentsActions"
import AddIcon from "@mui/icons-material/Add"
import { TextField } from "@mui/material";
import EditIcon from "@mui/icons-material/Edit"
import DeleteIcon from "@mui/icons-material/Delete"


function Comments({itinerary, cityid}){
    // console.log(itinerary);
    const [comments, setComments] = useState([])
    const [inputText, setInputText] = useState()
    const [modify, setModify] = useState()
    const [reload, setReload] = useState()
    const dispatch = useDispatch()
    // console.log(inputText);
    const user = useSelector(store=>store.userReducer.user)

    // let itineraryId = itinerary._id

    async function toAdd(event) {
        const commentData = {
            itineraryId: itinerary._id,
            comments: {
                comment: inputText,
                userid: user.id
            },
        }
        await dispatch(commentActions.addComment(commentData))
        setInputText("")
        setReload(!reload)
    }
    async function toModify(comments){
        const commentData = {
            commentId : comments._id,
            comment: modify
            }
        await dispatch(commentActions.modifyComment(commentData))
            setReload(!reload)
    }
    async function toDelete(comments){
        await dispatch(commentActions.deleteComment(comments._id))
        setReload(!reload)
    }

    useEffect(()=>{
        dispatch(itinerariesActions.findTinFromCity(cityid))
    },[reload])
    console.log(itinerary);
    return(
        <div>
            <div className="comments-container">
                {itinerary.comments.length>0?
                    user==null?
                    itinerary.comments.map(comments=>(
                        <div className="comment">
                                <p>{comments.comment}</p>
                                <p>{comments.date}</p>
                        </div>
                    ))
                        :
                        itinerary.comments.map(comments=>
                            (
                            user &&
                            user.id==comments.iduser?
                            (
                            <div className="comment">
                                <p>{comments.comment}</p>
                                <p>{comments.date}</p>
                                <textarea rows='2' onChange={(event) => setModify(event.target.value)} defaultValue={comments.comments} className='myInputforComment fredokaFont' />
                                <EditIcon id={comments._id} onClick={() => toModify(comments)} sx={{bgcolor: 'rgb(196, 165, 126)', '&:hover': {bgcolor: 'rgba(196, 165, 126, 0.7)'}, padding: '5px', margin: '5px', color: 'white', width: '30px', height: '30px', borderRadius: '15px'}} />
                                <DeleteIcon id={comments._id} onClick={() => toDelete(comments)} sx={{bgcolor: 'rgb(196, 165, 126)', '&:hover': {bgcolor: 'rgba(196, 165, 126, 0.7)'}, padding: '5px', marginTop: '5px', marginBottom: '5px', color: 'white', width: '30px', height: '30px', borderRadius: '15px'}} />
                            </div>
                            ):
                            (<div className="comment">
                                <p>{comments.comment}</p>
                                <p>{comments.date}</p>
                            </div>
                            ))):
                            <div>
                                <h1>there are no commentaries, be the first</h1>
                            </div>
                        
                }
            </div>
            <TextField id="standard-basic" label="Comment" variant="standard" onChange={(event) => setInputText(event.target.value)} value={inputText}/>
            <AddIcon onClick={toAdd}  />
        </div>
    )
}
export default Comments
