import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import activitiesActions from "../redux/actions/activitiesActions"
import itinerariesActions from "../redux/actions/itinerariesActions";

function Activities(){
    const id = useParams()
    const dispatch = useDispatch()
    const itinerary = useSelector(store=>store.itinerariesReducer.itinerary)
    const activity = useSelector(store=>store.activitiesReducer.activity)
    dispatch(itinerariesActions.findTinFromCity(id))
    // console.log(itinerary)
    // itinerary.map(it => (
    //     dispatch(activitiesActions.findActbyTin(it._id))
    //     ))
    return (
        <div className="activities">
            {activity.map(activity=>(
                <>
                <p>{activity.activity}</p>
                <div className="activity-photo">
                    <img src={activity.activityphoto} alt="la concha de tu madre velez" />
                </div>
                </>
            ))
            }
        </div>
    )
}
export default Activities
